.link {
  display: inline-flex;
  align-items: baseline;
  @include font(12,12);
  &::after {
    content: '';
    display: block;
    width: 8px;
    height: 8px;
    margin-left: 4px;
    border: 1px solid $black;
    border-top: none;
    border-left: none;
    transform: rotate(315deg);
  }
  &--gray {
    color: $gray;
    &::after{
      border-color: inherit;
    }
  }
  &:hover {
    text-decoration:underline;
  }
  &--button {
    width: 100%;
    height: 100%;
    &::after {
      display: none;
    }
  }
  &--blue {
    color: $dark-blue;
    &::after {
      display: none;
    }
  }
}

